// Surveyform shows a from for a user to add input
import _ from 'lodash';
import React, { Component } from 'react';
import { reduxForm, Field } from 'redux-form';
import SurveyField from './SurveyField';
import { Link } from 'react-router-dom';
import validateEmails from '../../utils/validateEmails';

const FIELDS = [
    { label: 'Survey Title', name: 'title' },
    { label: 'Subject Line', name: 'subject' },
    { label: 'Email Body', name: 'body' },
    { label: 'Recipient List', name: 'emails' }
]


class SurveyForm extends Component {
    renderFields(){
       return _.map(FIELDS, ({ label, name }) => {
        return( 
        <Field
            key={name}
            component={SurveyField}
            type="text"
            label={label}
            name={name}
         />
         );
       });
    }

    render() {
        return(
            <div>
                <form onSubmit={this.props.handleSubmit(this.props.onSurveySubmit)}>
                    {this.renderFields()}
                    <Link to="/surveys" className="red accent-2 btn waves-effect  waves-ripple white-text">
                        Cancel
                        <i className="material-icons right">clear</i>
                    </Link>
                    <button type="submit" className="blue accent-2 btn waves-effect waves-ripple white-text right">
                        Next
                        <i className="material-icons right">send</i>
                    </button>
                </form>
            </div>
        );
    }
}

function validate(values) {
    const errors = {};

    errors.emails = validateEmails(values.emails || '');

    if(!values.title) {
        errors.title = 'You must provide a title';
    }
    if(!values.subject) {
        errors.subject = 'You must provide a subject';
    }
    if(!values.body) {
        errors.body = 'You must provide a body';
    }
    

    

    return errors;
}

export default reduxForm({
    validate,
    form: 'surveyForm',
    des
})(SurveyForm);
