import React from 'react';
import { connect } from 'react-redux';
import _ from 'lodash';
import formFields from './formFields';

const SurveyFormReview = ({ onCancel, formValues }) => {
    const reviewFields = _.map(formFields, ({ name, label }) => {
        return (
            <div key={name}>
                 <ul className="collection grey lighten-4">
            <li className="collection-item avatar grey darken-2">
              <i className="material-icons circle white-text">assignment</i>
                <span className="title white-text">{label}</span>
                 <p className="white-text"> {formValues[name]} </p>
                 <a href="#!" className="secondary-content white-text"><i className="material-icons">assignment_turned_in</i></a>
             </li>
             </ul>
            </div>

        );
    });

    return(
            <div>
                <h5 className="header white-text">Please confirm your entries</h5>
               
                 {reviewFields}
               
               
                <button 
                className="amber white-text btn-flat"
                onClick={onCancel}
                >
                Back
                </button>
                <button className="green btn-flat white-text right">
                    Send
                    <i className="material-icons right">send</i>
                </button>
            </div>
    );
};

function mapStateToProps(state) {
    return { formValues: state.form.surveyForm.values };
}

export default connect(mapStateToProps)(SurveyFormReview);
